import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import data from '../data/datos.json';

function Blog() {
  const { id } = useParams<{ id: string }>();
  const productId = id ? parseInt(id, 10) : 0;
  const [categoria, setCategoria] = useState(1);

  useEffect(() => {
    if (productId <= 5) {
      setCategoria(1);
    } else if (productId === 6) {
      setCategoria(2);
    } else if (productId >= 7) {
      setCategoria(3);
    }
  }, [productId])

  console.log(categoria)
  function Busca(): any {
    switch (categoria) {
      case 1:
        return data.backend.find((proj: any) => proj.id === productId);
      case 2:
        return data.frontend.find((proj: any) => proj.id === productId);
      case 3:
        return data.Monoliticas.find((proj: any) => proj.id === productId);
      default:
        return null;
    }
  }

  const informacion = Busca();
  console.log(informacion)

  return (
    <div>
      {informacion ? (
        <div>{informacion.title}</div> // Aquí asumo que 'informacion' es un solo objeto
      ) : (
        <div>No se encontró el producto.</div>
      )}
    </div>
  )
}

export default Blog
